"use strict";

function swiperSlider(i) {
    jQuery(i).each(function(e) {
        new Swiper(i, {
            loop: !0,
            speed: 1e3,
            slidesPerView: 5,
            spaceBetween: 40,
            pagination: !1,
            centeredSlides: !1,
            breakpoints: {
                0: {
                    slidesPerView: 2
                },
                767: {
                    slidesPerView: 3
                },
                999: {
                    slidesPerView: 4
                },
                1149: {
                    slidesPerView: 5
                }
            },
            disableOnInteraction: !1,
            autoplay: !1,
            navigation: {
                nextEl: ".swiper-button-next",
                prevEl: ".swiper-button-prev"
            }
        })
    }), setTimeout(function() {
        window.dispatchEvent(new Event("resize"))
    }, 500)
}! function(e) {
    e(document).ready(function() {
        0 < e(".streamit-swiper").length && swiperSlider(".streamit-swiper")
    })
}(jQuery);